DROP TABLE IF EXISTS dish;
DROP TABLE IF EXISTS users;
DROP TABLE IF EXISTS comment_restaurants;
DROP TABLE IF EXISTS streets;



create table restaurants
(
    id integer PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
    name varchar(80),
    city varchar(80),
    address text,
    image_path text,
    slug varchar(20) UNIQUE,
    min_price int,
    avg_price int,
    rating int,
    count_rating int
);

create table dish
(
    id integer PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
    restaurant integer references restaurants,
    name varchar(80),
    description text,
    image_path text,
    calories integer,
    weight integer,
    price integer
);

CREATE TABLE users (
    id integer PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
    name VARCHAR(256) NOT NULL,
    email VARCHAR(256)  CHECK (email ~* '^[A-Za-z0-9._+%-]+@[A-Za-z0-9.-]+[.][A-Za-z]+$') NOT NULL UNIQUE,
    phone NUMERIC(11) CHECK(phone>79000000000 and phone <80000000000) NOT NULL UNIQUE,
    avatar VARCHAR(256)
);

